ArchitectDao.gFeeStructure = SELECT CAST(IF(f.`NewRegistrationFee` IS NULL,'0',f.`NewRegistrationFee`) AS UNSIGNED) registrationFee,f.`FirstRenewalFee` renewalFee,f.`Name` name,f.`RegistrationValidity` validaty FROM `crpservicefeestructure` f WHERE f.`Name` LIKE '%Architect%'

ArchitectDao.getMaxId = SELECT  A.ReferenceNo FROM crparchitect A ORDER BY A.ReferenceNo DESC LIMIT 1
ArchitectDao.getTaskList=SELECT a.`Name` serviceName,a.`ReferenceNo` applicationNo,a.`MobileNo` contactNo,a.`ApplicationDate` applicationDate,a.`CIDNo` serviceNo,b.`Name` AS appStatus FROM `crparchitect` a INNER JOIN `cmnlistitem` b ON b.`Id`  = a.`CmnApplicationRegistrationStatusId` INNER JOIN crparchitectappliedservice s ON s.`CrpArchitectId`=a.`Id` WHERE a.`SysLockedByUserId` IS NULL AND s.`CmnServiceTypeId`=? AND a.`CmnApplicationRegistrationStatusId`=? ORDER BY a.`ReferenceNo` DESC;
ArchitectDao.getMyTaskList=SELECT a.`Name` serviceName,a.`ReferenceNo` applicationNo,a.`MobileNo` contactNo,a.`ApplicationDate` applicationDate,a.`CIDNo` serviceNo,b.`Name` AS appStatus FROM `crparchitect` a INNER JOIN `cmnlistitem` b ON b.`Id`  = a.`CmnApplicationRegistrationStatusId` INNER JOIN crparchitectappliedservice s ON s.`CrpArchitectId`=a.`Id` WHERE a.`SysLockedByUserId`=? AND s.`CmnServiceTypeId`=? AND a.`CmnApplicationRegistrationStatusId`=? ORDER BY a.`ReferenceNo` DESC;

ArchitectDao.send2MyOrGroupTask = UPDATE crparchitect a SET a.`SysLockedByUserId` =:lockUserId  WHERE a.ReferenceNo =:appNo

ArchitectDao.getArchiectDtls=SELECT a.`CmnApplicationRegistrationStatusId` updateStatus,a.CrpArchitectId,a.`ReferenceNo`,a.`CIDNo` cidNo,a.`Name` fullname,i.`Name`salutation,d.`NameEn` dzongkhagId,a.`Gewog` gewog,a.`Village` village,c.`Name` countryId,ser.`Name` serviceSectorType,a.`Email` email,a.`MobileNo` mobileNo,a.`EmployerName` employeeName,a.`EmployerAddress` employeeAddress,q.`Name` qualificationId,a.`GraduationYear` graduationyr,a.`NameOfUniversity` universityName,uc.`Name` universityCountry,a.`VerifiedDate` verifcationdate,a.`RemarksByVerifier` verifierremarks,a.`RegistrationApprovedDate` approvaldate, a.`RemarksByApprover` approiverremarks,ss.`Name` serviceTypeId,a.`ARNo` cdbNo,a.`RegistrationExpiryDate` regExpDate,a.`ApplicationDate` applicationDate FROM crparchitect a LEFT JOIN `cmnlistitem` i ON i.`Id`=a.`CmnSalutationId` LEFT JOIN `cmndzongkhag` d ON d.`Id`=a.`CmnDzongkhagId` LEFT JOIN cmncountry c ON c.`Id`=a.`CmnCountryId` LEFT JOIN cmnlistitem ser ON ser.`Id`=a.`CmnServiceSectorTypeId` LEFT JOIN cmnlistitem q ON q.`Id`=a.`CmnQualificationId` LEFT JOIN cmncountry uc ON uc.`Id`=a.`CmnUniversityCountryId` LEFT JOIN `crparchitectappliedservice` s ON s.`CrpArchitectId`=a.`Id` LEFT JOIN crpservice ss ON ss.`Id`=s.`CmnServiceTypeId` WHERE a.`ReferenceNo`=?
ArchitectDao.getArchiectDoc=SELECT d.Id id,d.`DocumentName`,d.`DocumentPath`,d.`CreatedOn`,d.`FileType` FROM `crparchitectattachment` d WHERE d.`CrpArchitectId`=?

ArchitectDao.getarchitectregDetails=SELECT `ApplicationDate` applicationDate,CmnApplicationRegistrationStatusId updateStatus, CrpArchitectId,ReferenceNo,CIDNo cidNo,`Name` fullname,CmnSalutationId salutation,CmnDzongkhagId dzongkhagId, `Gewog` gewog,`Village` village,CmnCountryId countryId,CmnServiceSectorTypeId serviceSectorType,`Email` email,`MobileNo` mobileNo,`EmployerName` employeeName,`EmployerAddress` employeeAddress,CmnQualificationId qualificationId,`GraduationYear` graduationyr,`NameOfUniversity` universityName,CmnUniversityCountryId universityCountry,`RegistrationApprovedDate` approvaldate,`RemarksByApprover` approiverremarks,RegistrationExpiryDate regExpDate FROM crparchitect WHERE ReferenceNo=?
ArchitectDao.getPrintList=SELECT f.`ARNo` cdbNo,f.`ApplicationDate` applicationDate,f.`CIDNo` cidNo,f.`Email` email,f.`Name` fullname,f.`RegistrationApprovedDate` paymentReceiptDate,f.`MobileNo` mobileNo FROM crparchitectfinal f WHERE f.`CmnApplicationRegistrationStatusId`=?